import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import UploadPage from './UploadPage';
import GalleryPage from './GalleryPage';
import PhotoDetailsPage from './PhotoDetailsPage';
import './App.css';

function App() {
  const [photos, setPhotos] = useState([]);

  const addPhoto = (photo) => {
    setPhotos([...photos, photo]);
  };

  return (
    <Router>
      <Routes>
        <Route path="/" element={<UploadPage addPhoto={addPhoto} />} />
        <Route path="/gallery" element={<GalleryPage photos={photos} />} />
        <Route path="/details/:id" element={<PhotoDetailsPage photos={photos} />} />
      </Routes>
    </Router>
  );
}

export default App;
